releaseNotes.template:
{{#if compareUrl}}
    # [v{{nextRelease.version}}]({{compareUrl}}) ({{datetime "UTC:yyyy-mm-dd"}})
{{else}}
    # v{{nextRelease.version}} ({{datetime "UTC:yyyy-mm-dd"}})
{{/if}}

{{#with commits}}
    {{#if sparkles}}
        ## âœ¨ New Features
        {{#each sparkles}}
            - {{> commitTemplate}}
        {{/each}}
    {{/if}}

    {{#if bug}}
        ## 🐛 Bug Fixes
        {{#each bug}}
            - {{> commitTemplate}}
        {{/each}}
    {{/if}}

    {{#if ambulance}}
        ## 🚑 Critical Hotfixes
        {{#each ambulance}}
            - {{> commitTemplate}}
        {{/each}}
    {{/if}}

    {{#if lock}}
        ## 🔒 Security Issues
        {{#each lock}}
            - {{> commitTemplate}}
        {{/each}}
    {{/if}}

    {{#if boom}}
        ## 💥 Breaking Changes
        {{#each boom}}
            - {{> commitTemplate}}
        {{/each}}
    {{/if}}

    {{#unless ambulance}}
        ## Other changes
        {{#each this}}
            - {{> commitTemplate}}
        {{/each}}
    {{/unless}}

{{/with}}
