releaseNotes.template:
{{#if compareUrl}}
# [v{{nextRelease.version}}]({{compareUrl}}) ({{datetime "UTC:yyyy-mm-dd"}})
{{else}}
# v{{nextRelease.version}} ({{datetime "UTC:yyyy-mm-dd"}})
{{/if}}

{{#with commits}}
{{#if sparkles}}
## ✨ New Features
{{#each sparkles}}
- {{> commitTemplate}}
{{/each}}
{{/if}}

{{#if bug}}
## 🐛 Bug Fixes
{{#each bug}}
- {{> commitTemplate}}
{{/each}}
{{/if}}

{{#if ambulance}}
## 🚑 Critical Hotfixes
{{#each ambulance}}
- {{> commitTemplate}}
{{/each}}
{{/if}}

{{#if lock}}
## 🔒 Security Issues
{{#each lock}}
- {{> commitTemplate}}
{{/each}}
{{/if}}

{{#if boom}}
## 💥 Breaking Changes
{{#each boom}}
- {{> commitTemplate}}
{{/each}}
{{/if}}

{{#if zap}}
## ⚡ Enhancements
{{#each zap}}
- {{> commitTemplate}}
{{/each}}
{{/if}}

{{#if pencil}}
## 📝 Documentation Changes
{{#each pencil}}
- {{> commitTemplate}}
{{/each}}
{{/if}}

{{#if construction}}
## 🚧 Under Construction
{{#each construction}}
- {{> commitTemplate}}
{{/each}}
{{/if}}
{{/with}}
